INSERT INTO `wz_board_overview` VALUES (1, '研究院概况', '\n										<p style=\"text-indent:32px;line-height:39px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">综合性国家科学中心数据空间研究院是新一代信息技术数据空间领域的新型研发机构，依托合肥综合性国家科学中心，由安徽省人民政府发起的事业单位独立法人。坐落在江淮大地合肥高新技术开发区，毗邻中国科学技术大学高新校区，位于“科大硅谷”核心区。</span><br></p><p style=\"text-indent:32px;line-height:39px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">研究院由“国家最高科学技术奖”获得者王小谟院士出任首任院长，8位院士鼎力支持，汇聚全球顶尖科技人才，现有国家级青年人才10余人，力争5年内建成世界一流、国内顶尖的数据空间领域新型研发机构。研究院以“数据重构网络空间”为核心理念，以“数据空间生态系统”为核心体系，有效整合科研资源开展理论、技术和产业孵化。</span></p><p style=\"text-indent:32px;line-height:39px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">研究院按照研究中心+研究院+研究院公司布局，在中国科大设立数据空间研究中心（院士工作站），聚焦基础理论研究；院本部聚焦应用基础研究和技术创新，包括数理研究、数据科学、算法安全、网络空间、计算社会学等方向；研究院公司致力于产业化，设立产业发展基金，专注培育大数据创新产业。研究院致力于引领数据空间领域学科前沿和技术创新方向，推动战略性新兴产业发展。</span></p><p><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">&nbsp;</span></p><p><br></p>				', 1, 0, NULL, NULL, '2022-11-22 15:19:41', NULL, '2022-11-22 15:19:35');

