DROP TABLE IF EXISTS `wz_news`;
CREATE TABLE `wz_news`  (
  `NID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `TITLE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `PHOTO` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片',
  `RESUME` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '简介',
  `CONTENT` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `RELEASE_TIME` date NULL DEFAULT NULL COMMENT '发布时间',
  `SOURCE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章来源',
  `DISABLE_FLAG` bigint(20) NOT NULL DEFAULT 1 COMMENT '是否启用标识;字典：1启用，0禁用',
  `DELETE_FLAG` bigint(20) NOT NULL DEFAULT 0 COMMENT '是否删除标识;字典：1删除，0未删除',
  `SORT_NUM` bigint(20) NULL DEFAULT NULL COMMENT '排序编号',
  `CREATED_BY` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建人',
  `CREATED_TIME` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `UPDATED_BY` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '更新人',
  `UPDATED_TIME` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`NID`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 27 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '新闻中心' ROW_FORMAT = Dynamic;

