INSERT INTO `wz_notice` VALUES (3, '数据空间研究院“网络空间研究所”场地租赁单一来源采购公示', NULL, '2022-09-13', '<p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\"></span></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:29px;font-family: 宋体\">数据空间研究院“网络空间研究所”场地租赁</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:29px;font-family: 宋体\">单一来源采购公示</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:29px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:27px\"><strong><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">一、项目内容</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">（一）项目名称</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">数据空间研究院“网络空间研究所”场地租赁项目</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">（二）项目内容</span></p><p style=\"text-indent:68px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">办公房屋租赁</span></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">二、拟采购的标的物说明</span></strong></p><p style=\"text-align:left;text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">（一）拟选址场地位于北京市北五环外海淀区西小口路66号中关村东升科技园·北领地B-6楼A座7层A708、A709、A710室 ，建筑面积2144.18平方米。</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">（二）租赁期限及租金</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">1.</span><span style=\"font-size:21px;font-family:仿宋\">租赁期限为3年，自2022年10月1日至2025年9月30日止。</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">2.</span><span style=\"font-size:21px;font-family:仿宋\">房屋租金及物业管理费：17936923.37元</span></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">三、采用单一来源采购方式的原因及相关说明</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">数据空间研究院“网络空间研究所”场地租赁采用单一来源采购的方式进行房屋租赁。</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">由于网络空间研究所需要在北京市海淀区范围内进行选址， 考虑三年内的办公需要，新办公场地需具备70人以上的研发团队，按照人均7平米使用面积，需要具备办公面积约490平米，展厅面积约500平米，会议室约150平米，独立办公室约300平米；办公场地需要具备独立办公室、独立会议室、设备机房、宽带网络、中央空调等基础条件，综上预计需要承租面积约2000平米。</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">经调研，拟定中关村东升科技园·北领地作为新办公场地，中关村东升科技园位于海淀、朝阳、昌平三区交界，区划面积4.53 平方公里。作为中关村科学城的一部分，是第一个以“中关村”冠名的乡镇级园区，是海淀区东升镇打造的向“高精尖”转型的代表。经市场调研，满足上述办公条件的海淀区中关村附近办公场地租金一般在每天每平米10～15元，且起租期多为1-2年。租赁东升科技园B-6号楼A座7层整层作为办公用房，该租区报价为10元/天/平米（含物业费），经多次与东升科技园项目进行沟通，最终给予的优惠价格为8元/天/平米（含物业费），租赁期限为3年，租金三年内无递增。经市场调研东升科技园B-6号楼实际成交价格为8.5-9.2元/天/平米左右，且园区内综合配套丰富，交通便利，距离地铁八号线仅步行约5分钟左右，另外园区可提供周边公租房，用以解决核心技术人员的住房需求。</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">综上考虑，选定东升科技园B-6号楼A座7层整层作为办公用房作为网络空间研究所的办公用房。</span></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">四、供应商相关信息</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">供应商名称：北京东升博展科技有限公司</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">注册地址：北京市海淀区西小口路66号中关村东升科技园B区2号楼地下一层103室</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">统一社会信用代码：911101087400590913</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">法定代表人：卢维维</span></p><p class=\"MsoListParagraph\" style=\";text-indent:47px;line-height:27px\"><strong><span style=\"font-size:21px;font-family:仿宋\">五、公示的期限</span></strong></p><p class=\"MsoListParagraph\" style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">5</span><span style=\"font-size:21px;font-family:仿宋\">个工作日，2022年9月14日至2022年9月20日</span></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">六、采购需求部门的联系人和联系电话</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">黄旭东，0551-65361800，18226163907</span></p><p style=\"text-indent:47px;line-height:27px\"><strong><span style=\"font-size: 21px;font-family:仿宋\">七、纪检审计法务部的联系人和联系电话</span></strong></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">王建，0551-65361900，18010888236</span></p><p style=\"text-indent:47px;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"text-indent:47px;line-height:27px\"><span style=\"font-size: 21px;font-family:仿宋\">现将以上情况公示，任何供应商、单位或个人如有异议，请于公示期内携书面意见材料与有关联系人联系，逾期将不再受理。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:48px;text-indent:0;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:48px;text-indent:0;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:48px;text-indent:0;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:48px;text-indent:0;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:48px;text-indent:0;line-height:27px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left: 48px; text-indent: 0px; line-height: 27px; text-align: right;\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left: 48px; text-indent: 0px; line-height: 27px; text-align: right;\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">数据空间研究院</span></p><p class=\"MsoListParagraph\" style=\"margin-left: 48px; text-indent: 0px; line-height: 27px; text-align: right;\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2022</span><span style=\"font-size:21px;font-family:仿宋\">年9月13日</span></p><p><br></p>', 1, 0, NULL, NULL, NULL, NULL, NULL);

