INSERT INTO `wz_personnel_overview` VALUES (1, '李向阳', '教授', '/uploads/20221118/0914ae7e2e7224c2ccad196ea6a03fa9.jpeg', '合肥综合性国家科学中心数据空间研究院首席科学家。中国科学技术大学教授、博士生导师，现任中国科学技术大学信息与智能学部常务副部长、计算机科学与技术学院执行院长，中国科学院无线光电通信重点实验室主任，前ACM中国理事会共同主席。自2000年以来已在高水平国际期刊及会议累计发表近500篇学术论文（其中CCFA类论文160余篇...', '研究人员', NULL, '2022-11-18', '<p style=\"text-indent: 2em; line-height: 1.5em; text-align: center;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><img src=\"/uploads/20221118/0914ae7e2e7224c2ccad196ea6a03fa9.jpeg\" title=\"\" alt=\"\" width=\"350\" height=\"530\" style=\"width: 350px; height: 530px;\"></span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><br></span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">合肥综合性国家科学中心数据空间研究院首席科学家。</span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"></span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">中国科学技术大学</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">教授、</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">博士生导师</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">，现任中国科学技术大学信息与智能学部常务副部长、计算机科学与技术学院执行院长，</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">中国科学院</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">无线光电通信重点实验室主任，前ACM中国理事会共同主席。</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"></span></p><p style=\"line-height: 1.5em\"><br></p><p style=\"line-height: 1.5em; margin-top: 5px; text-indent: 2em;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">自2000年以来已在高水平国际期刊及会议累计发表近500篇学术论文（其中CCF A类论文160余篇），包括移动计算/无线网络领域著名的会议ACM MobiCom论文16篇，谷歌学术论文引用总数超过22000余次，H-Index 81, 近10次获得国际会议最佳论文奖，例如ACM MobiCom 2014、IEEE GlobeCom 2015等。获批国内外专利50余项。</span></p><p style=\"line-height: 1.5em; margin-top: 5px;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><br></span></p><p style=\"line-height: 1.5em; text-indent: 2em;\"><br></p><p style=\"line-height: 1.5em; text-indent: 2em;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">先后主持了国家自然科学基金委杰出青年基金，国家重点研发计划，基金委人工智能应急项目，美国基金委项目20余项，另外和多家国内企业有横向合作科研项目。独立/合作指导博士50余名，大部分在美国和中国高校担任教职。撰写了无线网络领域专著《Wireless Ad Hoc and Sensor Networks: Theory and Applications》（2008年出版）。曾经担任《IEEE/ACMTransactions on Networking》《IEEE Transactions on Mobile Computing》《IEEE Transactionson Parallel and Distributed Systems》等期刊的编委，和算法百科全书《Encyclopediaof Algorithms》无线网络学科方向的编辑。曾经担任了多个知名国际学术会议程序委员会主席或者大会主席（如ACM MobiHoc，IEEE MASS等），多年来担任本领域的重要学术会议的程序委员会成员（如ACM MobiCom，IEEE INFOCOM等）。</span></p>', 1, 0, 1, NULL, '2022-11-10 16:15:59', NULL, '2022-11-22 16:16:02');
INSERT INTO `wz_personnel_overview` VALUES (2, '刘翔龙', '教授', '/uploads/20221118/2348b4ee900f9e6f9702f6ac19a8e112.jpeg', '合肥综合性国家科学中心数据空间研究院算法安全团队负责人。教授，博士生导师，国家优秀青年科学基金获得者，现任软件开发环境国家重点实验室副主任、北京航空航天大学计算机学院院长助理，主要研究大数据检索、大规模视觉分析、可信赖深度学习。近年来，主持国家自然科学基金、国防科技创新重点项目、科技创新2030重大项目等多项国家课题；...', '研究人员', NULL, '2022-11-18', '<p style=\"text-indent: 2em; line-height: 1.5em; text-align: center;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><img src=\"/uploads/20221118/2348b4ee900f9e6f9702f6ac19a8e112.jpeg\" title=\"\" alt=\"\" width=\"350\" height=\"492\" style=\"width: 350px; height: 492px;\"></span></p><p style=\"text-indent: 2em; line-height: 1.5em;\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><br></span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">合肥综合性国家科学中心数据空间研究院算法安全团队负责人。</span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">教授，博士生导师，国家优秀青年科学基金获得者，现任软件开发环境国家重点实验室副主任、</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">北京航空航天大学</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">计算机学院院长助理，主要研究大数据检索、大规模视觉分析、可信赖深度学习。</span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><br></span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">近年来，主持国家自然科学基金、国防科技创新重点项目、科技创新2030重大项目等多项国家课题；发表IEEE CVPR、ICCV等国际顶级会议及期刊论文60余篇。担任Pattern Recognition、IET Image Processing等SCI期刊编委，ACM MM 2019/2020领域主席，国家新一代人工智能产业创新联盟启智开源开放平台技术委员会委员。</span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"><br></span></p><p style=\"text-indent: 2em;line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">指导学生获得新一代人工智能产业技术创新战略联盟OpenI启智社区首届优秀开源项目、优秀开发者、PCM 2018最佳学生论文、北航研究生十佳等。</span></p><p style=\"line-height: 1.5em\"><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">&nbsp;</span></p><p><strong><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\">获奖情况：</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px\"><span style=\"font-size: 19px;font-family: \'Times New Roman\', serif;color: rgb(51, 51, 51)\">1.<span style=\"font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size: 19px;font-family: \'Times New Roman\', serif;color: rgb(51, 51, 51);background: white\"></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 19px; text-indent: 32px; background-color: rgb(255, 255, 255);\">陕西省自然科学一等奖</span><span style=\"font-size: 19px;font-family: 宋体;color: rgb(51, 51, 51);background: white\"></span></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px\"><span style=\"font-size: 19px;font-family: \'Times New Roman\', serif;color: rgb(51, 51, 51)\">2.<span style=\"font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"font-size: 19px;font-family: \'Times New Roman\', serif;color: rgb(51, 51, 51);background: white\"></span><span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 19px; text-indent: 32px; background-color: rgb(255, 255, 255);\">北京市科技新星</span></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px\"><span style=\"font-size: 19px;font-family: \'Times New Roman\', s