DROP TABLE IF EXISTS `wz_product`;
CREATE TABLE `wz_product`  (
  `NID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `NAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '产品名称',
  `LINK` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '路由',
  `CONTENT` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `DISABLE_FLAG` bigint(20) NOT NULL DEFAULT 1 COMMENT '是否启用标识;字典：1启用，0禁用',
